import NavLinks from "./NavLinks";
import classes from './NavBar.module.css';
import {RxHamburgerMenu} from 'react-icons/rx'
import { useState } from "react";
import {GrClose} from 'react-icons/gr'

const MobileNavigation = () => {

    const [open, setOpen] = useState(false); 

    const hamburgerIcon =  <RxHamburgerMenu className={classes.Hamburger} 
                            size='40px' color='white'
                            onClick={() => setOpen(!open)}
                            /> 

    const closedIcon = <GrClose className={classes.Hamburger} 
                        size='40px' color='white'
                        onClick={() => setOpen(!open)}
                        /> 

    const closedMobileMenu = () => setOpen(false); 

    return ( 
        <nav className={classes.MobileNavigation}>
           {open ? closedIcon : hamburgerIcon}
            {open &&  <NavLinks /> }
        </nav>
     );
}

export default MobileNavigation;
